const yuemeiRender = require('./yuemei');

const backgroundTexture = 'data:image/png;base64,';
const logoTexture = 'data:image/png;base64,';

const styleHtml =
    '<style>' +
        '.mdjFadeInHide {visibility:hidden}' +
        '.mdjFadeIn {' +
            'animation-name:mdjFadeIn;-webkit-animation-name:mdjFadeIn;' +
            'animation-fill-mode:both;' +
            'animation-duration:1s;-webkit-animation-duration:1s;' +
            'animation-timing-function:ease;-webkit-animation-timing-function:ease;' +
            'visibility:visible !important;}' +
        '@keyframes mdjFadeIn {0% {transform:scale(1.6);} 100% {transform:scale(1);}}' +
        '@-webkit-keyframes mdjFadeIn { 0% {-webkit-transform:scale(1.6);} 100% {-webkit-transform:scale(1);-webkit-opacity:1;}}' +
    '</style>';
const containerTmpl =
    '<div id="mdjYuemeiContainer" class="mdjFadeInHide" style="display:flex;flex-direction:column;position:relative;overflow:hidden;width:100%;height:100%;background:black;">' +
        '<div style="width:100%;position:absolute;top:__MDJ_PHOTO_TOP__px;height:__MDJ_PHOTO_HEIGHT__px;overflow:hidden;">' +
            '<img style="width:100%;position:absolute;top:0;" src="__MDJ_PHOTO__" alt="photo">' +
            '<div style="width:100%;padding-top:16%;position:absolute;bottom:0;background:-webkit-linear-gradient(top,rgba(0,0,0,0),rgba(0,0,0,1));background:linear-gradient(top,rgba(0,0,0,0),rgba(0,0,0,1));background:-moz-linear-gradient(top,rgba(0,0,0,0),rgba(0,0,0,1));"></div>' +
        '</div>' +
        '<div style="width:100%;height:100%;position:absolute;top:0;left:0;background-color:rgba(0,0,0,0.2);"></div>' +
        '<img style="width:100%;position:absolute;bottom:0;" src="__MDJ_BACKGROUND__" alt="background">' +
        '<div id="mdjYuemeiLottie" style="position:absolute;top:__MDJ_PHOTO_TOP__px;width:100%;height:__MDJ_LOTTIE_HEIGHT__px"></div>' +
        '<div style="position:absolute;top:3%;left:5%;width:30%">' +
            '<img style="width:100%" src="__MDJ_LOGO__" alt="logo">' +
        '</div>' +
    '</div>';

const styleInject = () => {
    // 检查lottie是否存在
    if (!window.lottie) {
        console.warn('mdjYuemeiAnimation: Lottie not found in window context, please import before this script');
    }

    document.head.insertAdjacentHTML('beforeend', styleHtml);
};

styleInject();

let imgSize = {
	w:600,
	h:1500
}
const mdjYuemeiAnimation = (domObj, apiRet, callback) => {
    let domStyles = window.getComputedStyle(domObj);
    let domSize = {
        w: parseInt(domStyles.width),
        h: parseInt(domStyles.height),
    };

    let imageH = imgSize.h * domSize.w / imgSize.w;

    // 图片高度大于绘图区剪掉1/3宽度，需要'裁剪'图片，否则cutH=0，约等于没有变化
    let cutH = (imageH - domSize.h ) / 2;
    let calc = {
        photoTop: -cutH,
        photoHeight: imageH - cutH, // 上下裁剪的部分实际上是一样高的，所以这里砍掉高度差的一半
        lottieHeight: domSize.w * 1624 / 750, // lottie的高度，直接用宽度按750/1624计算出来
        animationSize: {
            w: domSize.w,
            h: domSize.h + cutH, // 实际动画区域的高度是dom高度+被遮掉的部分
        }
    };

    console.log('mdjYuemeiAnimation: position calc', calc);

    let containerHtml = containerTmpl;

    containerHtml = containerHtml.replace(/__MDJ_PHOTO_TOP__/g, calc.photoTop);
    containerHtml = containerHtml.replace(/__MDJ_PHOTO_HEIGHT__/g, calc.photoHeight);
    containerHtml = containerHtml.replace(/__MDJ_LOTTIE_HEIGHT__/g, calc.lottieHeight);

    containerHtml = containerHtml.replace('__MDJ_PHOTO__', apiRet.align_img);
    containerHtml = containerHtml.replace('__MDJ_BACKGROUND__', backgroundTexture);
    containerHtml = containerHtml.replace('__MDJ_LOGO__', logoTexture);

    domObj.innerHTML = containerHtml;

    let containerObj = document.getElementById('mdjYuemeiContainer');
    let lottieParams = {
        container: document.getElementById('mdjYuemeiLottie'),
        renderer: 'svg',
        loop: false,
        autoplay: false,
        animationData: yuemeiRender(apiRet, calc.animationSize),
        rendererSettings: {
            progressiveLoad: true,
        },
    };

    let lottieObj = lottie.loadAnimation(lottieParams);
    lottieObj.onComplete = () => {
        console.log('mdjYuemeiAnimation: done',lottieObj);
        if (callback) {
            callback(domObj);
        }
    }

    containerObj.addEventListener('webkitAnimationEnd', (evt) => {
        console.log('mdjYuemeiAnimation: play',lottieObj);
        lottieObj.play();
    }, false);
    containerObj.className = 'mdjFadeIn';
};

module.exports = mdjYuemeiAnimation;
